import React, { useState, useEffect, useCallback, useRef } from 'react';
import { db } from '../firebase';
import { collection, addDoc, query, orderBy, limit, getDocs, where, onSnapshot } from 'firebase/firestore';
import '../styles/SnakeGame.css';

// „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥Âêç„ÇíÂèñÂæó„Åô„ÇãÈñ¢Êï∞„ÇíËøΩÂä†
const getCollectionName = (mode, difficulty) => {
  if (mode === 'difficulty') {
    switch (difficulty) {
      case 'beginner':
        return 'scoreslow';
      case 'intermediate':
        return 'scoresmiddle';
      case 'advanced':
        return 'scoreshigh';
      default:
        return 'scores';
    }
  }
  return 'scores';
};

const SnakeGame = ({ mode = 'normal', difficulty = 'normal', settings = null, onGameOver = null, selectedItems = [] }) => {
  const gridSize = 20;
  const centerX = Math.floor(gridSize / 2);
  const centerY = Math.floor(gridSize / 2);

  // initialSnake„ÇíuseMemo„ÅßÊúÄÈÅ©Âåñ
  const initialSnake = React.useMemo(() => [
    [centerX, centerY],     // È†≠
    [centerX - 1, centerY], // ËÉ¥‰Ωì
    [centerX - 2, centerY]  // Â∞æ
  ], [centerX, centerY]);

  const [snake, setSnake] = useState(initialSnake);
  const [food, setFood] = useState([0, 0]);
  const [specialFood, setSpecialFood] = useState(null); // { type: 'yellow' | 'blue' | 'black', position: [x, y] }
  const [direction, setDirection] = useState('RIGHT');
  const directionRef = useRef(direction);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [highScore, setHighScore] = useState(() => {
    const saved = localStorage.getItem('snakeHighScore');
    return saved ? parseInt(saved) : 0;
  });
  const [topScores, setTopScores] = useState([]);
  const [timeLeft, setTimeLeft] = useState(mode === 'time' ? settings.initialTime : null);
  const [isTimeUp, setIsTimeUp] = useState(false);
  const [items, setItems] = useState([]);

  // „Ç¢„Ç§„ÉÜ„É†„ÅÆÁä∂ÊÖã„ÇíÁÆ°ÁêÜ
  const [activeItems, setActiveItems] = useState(selectedItems.map(item => ({
    id: item,
    isActive: false,
    timeLeft: 0
  })));

  const calculateSpeed = useCallback((score) => {
    let speed = 200 - Math.floor(score / 5) * 10;

    // „Çπ„Éî„Éº„Éâ„Éñ„Éº„Çπ„Éà„ÅåÊúâÂäπ„Å™Â†¥Âêà„ÄÅÈÄüÂ∫¶„Çí2ÂÄç„Å´
    if (activeItems.find(item => item.id === 'speed' && item.isActive)) {
      speed = Math.max(30, speed / 2);
    }

    return Math.max(60, speed);
  }, [activeItems]);

  // generateFood„ÇíÂÖà„Å´ÂÆöÁæ©
  const generateFood = useCallback(() => {
    const getRandomPosition = () => [
      Math.floor(Math.random() * gridSize),
      Math.floor(Math.random() * gridSize),
    ];

    const isPositionOccupied = (pos) => 
      snake.some(segment => segment[0] === pos[0] && segment[1] === pos[1]);

    let newFood;
    do {
      newFood = getRandomPosition();
    } while (isPositionOccupied(newFood));

    setFood(newFood);
  }, [snake, gridSize]);

  // ÁâπÊÆä„Ç®„Çµ„ÅÆÁîüÊàêÈñ¢Êï∞
  const generateSpecialFood = useCallback(() => {
    if (mode !== 'time' || !isPlaying) return;

    // Êó¢„Å´ÁâπÊÆä„Ç®„Çµ„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅØÁîüÊàê„Åó„Å™„ÅÑ
    if (specialFood) return;

    const types = ['yellow', 'blue', 'black'];
    const randomType = types[Math.floor(Math.random() * types.length)];

    const getRandomPosition = () => [
      Math.floor(Math.random() * gridSize),
      Math.floor(Math.random() * gridSize),
    ];

    // Êó¢Â≠ò„ÅÆ„Éò„Éì„ÄÅÈÄöÂ∏∏„Ç®„Çµ„Å®„ÅÆË°ùÁ™Å„ÉÅ„Çß„ÉÉ„ÇØ
    const isPositionOccupied = (pos) => 
      snake.some(segment => segment[0] === pos[0] && segment[1] === pos[1]) ||
      (food[0] === pos[0] && food[1] === pos[1]);

    let newFood;
    do {
      newFood = getRandomPosition();
    } while (isPositionOccupied(newFood));

    setSpecialFood({ type: randomType, position: newFood });

    // 5ÁßíÂæå„Å´ÁâπÊÆä„Ç®„Çµ„ÇíÊ∂à„Åô
    const timer = setTimeout(() => {
      setSpecialFood(null);
    }, 5000);

    return () => clearTimeout(timer);
  }, [snake, food, gridSize, mode, isPlaying, specialFood]);

  // ÁâπÊÆä„Ç®„Çµ„ÅÆÂäπÊûú„ÇíÂá¶ÁêÜ
  const handleSpecialFoodEffect = useCallback((type) => {
    switch (type) {
      case 'yellow':
        setScore(s => s + 3); // ÈªÑËâ≤„ÅØ3ÁÇπËøΩÂä†
        break;
      case 'blue':
        setTimeLeft(prev => prev + 5); // Èùí„ÅØÊôÇÈñì5ÁßíËøΩÂä†
        break;
      case 'black':
        setSnake(prev => { // Èªí„ÅØ„Éò„Éì„ÅÆÈï∑„Åï„Çí5Â¢ó„ÇÑ„Åô
          const tail = prev[prev.length - 1];
          const newSegments = Array(5).fill(tail);
          return [...prev, ...newSegments];
        });
        break;
      default:
        break;
    }
  }, []);

  // resetGame„ÅÆÂÆöÁæ©
  const resetGame = useCallback(() => {
    setSnake(initialSnake);
    setDirection('RIGHT');
    setGameOver(false);
    setScore(0);
    setIsPlaying(false);
    setFood([0, 0]); // „Ç®„Çµ„ÇíÂàùÊúü‰ΩçÁΩÆ„Å´Êàª„ÅôÔºàÊ¨°ÂõûÈñãÂßãÊôÇ„Å´Êñ∞„Åó„ÅÑ‰ΩçÁΩÆ„ÅåÁîüÊàê„Åï„Çå„ÇãÔºâ
  }, [initialSnake]);

  useEffect(() => {
    directionRef.current = direction;
  }, [direction]);

  const moveSnake = useCallback(() => {
    if (!isPlaying || gameOver) return;

    setSnake(prevSnake => {
      const head = [...prevSnake[0]];

      switch (directionRef.current) {
        case 'UP':
          head[1] -= 1;
          break;
        case 'DOWN':
          head[1] += 1;
          break;
        case 'LEFT':
          head[0] -= 1;
          break;
        case 'RIGHT':
          head[0] += 1;
          break;
        default:
          break;
      }

      // Â£Å„ÇÑ„Éò„ÉìËá™Ë∫´„Å®„ÅÆË°ùÁ™ÅÂà§ÂÆö
      if (checkCollision(head)) {
        setGameOver(true);
        setIsPlaying(false);
        return prevSnake;
      }

      const newSnake = [head];

      // ÈÄöÂ∏∏„ÅÆ„Ç®„Çµ„Å®„ÅÆË°ùÁ™ÅÂà§ÂÆö
      if (head[0] === food[0] && head[1] === food[1]) {
        setScore(s => s + 1);
        generateFood();
        newSnake.push(...prevSnake);
      } else {
        newSnake.push(...prevSnake.slice(0, -1));
      }

      // ÁâπÊÆä„Ç®„Çµ„Å®„ÅÆË°ùÁ™ÅÂà§ÂÆö„ÇíËøΩÂä†
      if (specialFood && head[0] === specialFood.position[0] && head[1] === specialFood.position[1]) {
        handleSpecialFoodEffect(specialFood.type);
        setSpecialFood(null);
      }

      return newSnake;
    });
  }, [isPlaying, gameOver, food, generateFood, gridSize, specialFood, handleSpecialFoodEffect, checkCollision]);

  // „Ç®„Çµ„ÅÆÁîüÊàê„ÇíÁÆ°ÁêÜ„Åô„ÇãuseEffect
  useEffect(() => {
    // „Ç®„Çµ„ÅåÂàùÊúü‰ΩçÁΩÆ[0,0]„ÅÆÊôÇ„ÅÆ„ÅøÁîüÊàê
    if (food[0] === 0 && food[1] === 0) {
      generateFood();
    }
  }, [generateFood, food]);

  // ÁâπÊÆä„Ç®„Çµ„ÅÆÁîüÊàê„Çø„Ç§„Éû„Éº
  useEffect(() => {
    if (mode !== 'time' || !isPlaying) {
      setSpecialFood(null); // „Éó„É¨„Ç§‰∏≠„Åß„Å™„ÅÑÂ†¥Âêà„ÅØÁâπÊÆä„Ç®„Çµ„ÇíÊ∂à„Åô
      return;
    }

    // 10Áßí„Åî„Å®„Å´ÁâπÊÆä„Ç®„Çµ„ÇíÁîüÊàê
    const interval = setInterval(() => {
      generateSpecialFood();
    }, 10000); // 10ÁßíÈñìÈöî

    return () => {
      clearInterval(interval);
    };
  }, [mode, isPlaying, generateSpecialFood]);

  // „Ç¢„Ç§„ÉÜ„É†„ÅÆÂäπÊûú„ÇíÂá¶ÁêÜ„Åô„ÇãÈñ¢Êï∞
  const handleItemEffect = useCallback((itemId) => {
    switch (itemId) {
      case 'speed':
        setActiveItems(prev => prev.map(item => 
          item.id === 'speed' ? { ...item, isActive: true, timeLeft: 10 } : item
        ));
        break;
      case 'shield':
        setActiveItems(prev => prev.map(item => 
          item.id === 'shield' ? { ...item, isActive: true, timeLeft: 5 } : item
        ));
        break;
      case 'magnet':
        setActiveItems(prev => prev.map(item => 
          item.id === 'magnet' ? { ...item, isActive: true, timeLeft: 10 } : item
        ));
        break;
      case 'double':
        setActiveItems(prev => prev.map(item => 
          item.id === 'double' ? { ...item, isActive: true, timeLeft: 15 } : item
        ));
        break;
      default:
        break;
    }
  }, []);

  // „Ç¢„Ç§„ÉÜ„É†„ÅÆÂäπÊûúÊôÇÈñì„ÇíÁÆ°ÁêÜ
  useEffect(() => {
    if (!isPlaying || activeItems.every(item => !item.isActive)) return;

    const timer = setInterval(() => {
      setActiveItems(prev => prev.map(item => {
        if (!item.isActive) return item;
        const newTimeLeft = item.timeLeft - 1;
        return {
          ...item,
          isActive: newTimeLeft > 0,
          timeLeft: Math.max(0, newTimeLeft)
        };
      }));
    }, 1000);

    return () => clearInterval(timer);
  }, [isPlaying, activeItems]);

  // „Ç≠„Éº„Éú„Éº„ÉâÂÖ•Âäõ„ÅÆÂá¶ÁêÜ„Çí‰øÆÊ≠£
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'ArrowUp' || 
          e.key === 'ArrowDown' || 
          e.key === 'ArrowLeft' || 
          e.key === 'ArrowRight' || 
          e.code === 'Space' ||
          e.key === '1' ||
          e.key === '2') {
        e.preventDefault();
      }

      if (!isPlaying) return;

      // „Ç¢„Ç§„ÉÜ„É†„ÅÆ‰ΩøÁî®Ôºà1„Ç≠„Éº„Åß1„Å§ÁõÆ„ÄÅ2„Ç≠„Éº„Åß2„Å§ÁõÆ„ÅÆ„Ç¢„Ç§„ÉÜ„É†Ôºâ
      if (e.key === '1' && activeItems[0] && !activeItems[0].isActive) {
        handleItemEffect(activeItems[0].id);
      }
      if (e.key === '2' && activeItems[1] && !activeItems[1].isActive) {
        handleItemEffect(activeItems[1].id);
      }

      // „Ç¢„Ç§„ÉÜ„É†ÂäπÊûú„ÅÆÈÅ©Áî®
      if (e.code === 'Space') {
        if (gameOver) {
          resetGame();
        } else {
          setIsPlaying(prev => !prev);
        }
        return;
      }

      switch (e.key) {
        case 'ArrowUp':
          if (directionRef.current !== 'DOWN') setDirection('UP');
          break;
        case 'ArrowDown':
          if (directionRef.current !== 'UP') setDirection('DOWN');
          break;
        case 'ArrowLeft':
          if (directionRef.current !== 'RIGHT') setDirection('LEFT');
          break;
        case 'ArrowRight':
          if (directionRef.current !== 'LEFT') setDirection('RIGHT');
          break;
        default:
          break;
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    const gameInterval = setInterval(moveSnake, calculateSpeed(score));

    return () => {
      document.removeEventListener('keydown', handleKeyPress);
      clearInterval(gameInterval);
    };
  }, [moveSnake, gameOver, isPlaying, score, calculateSpeed, resetGame, activeItems, handleItemEffect]);

  const startGame = () => {
    if (!isPlaying && !gameOver) {
      if (food[0] === 0 && food[1] === 0) {
        generateFood(); // „Ç®„Çµ„ÅåÂàùÊúü‰ΩçÁΩÆ„ÅÆÂ†¥Âêà„ÅÆ„ÅøÁîüÊàê
      }
      setIsPlaying(true);
    }
  };

  // „Çπ„Ç≥„Ç¢„Çí‰øùÂ≠ò„Åô„ÇãÈñ¢Êï∞„Çí‰øÆÊ≠£
  const updateScores = useCallback(async (newScore) => {
    try {
      let collectionName;

      // „É¢„Éº„Éâ„Å´Âøú„Åò„Åü„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥Âêç„ÇíË®≠ÂÆö
      if (mode === 'time') {
        if (timeLeft > 0) return; // ÊôÇÈñì„ÅåÊÆã„Å£„Å¶„ÅÑ„ÇãÂ†¥Âêà„ÅØË®òÈå≤„Åó„Å™„ÅÑ
        collectionName = 'scorestime';
      } else if (mode === 'difficulty') {
        collectionName = getCollectionName(mode, difficulty);
      } else if (mode === 'item') {
        collectionName = 'scoresitem'; // „Ç¢„Ç§„ÉÜ„É†„É¢„Éº„ÉâÁî®„ÅÆ„Ç≥„É¨„ÇØ„Ç∑„Éß„É≥
      } else {
        collectionName = 'scores'; // ÈÄöÂ∏∏„É¢„Éº„Éâ
      }

      const scoresRef = collection(db, collectionName);
      const scoreData = {
        score: newScore,
        date: new Date(),
        items: selectedItems || [] // „Ç¢„Ç§„ÉÜ„É†„É¢„Éº„Éâ„ÅÆÂ†¥Âêà„ÄÅÈÅ∏Êäû„Åó„Åü„Ç¢„Ç§„ÉÜ„É†„ÇÇ‰øùÂ≠ò
      };

      await addDoc(scoresRef, scoreData);
    } catch (error) {
      console.error('Error saving score:', error);
    }
  }, [mode, difficulty, timeLeft, selectedItems]);

  // Firestore„ÅÆ„É™„Ç¢„É´„Çø„Ç§„É†„É™„Çπ„Éä„Éº„ÇíË®≠ÂÆö
  useEffect(() => {
    let collectionName;

    // „Ç≥„É¨„ÇØ„Ç∑„Éß„É≥Âêç„ÅÆË®≠ÂÆö
    if (mode === 'time') {
      collectionName = 'scorestime';
    } else if (mode === 'difficulty') {
      collectionName = getCollectionName(mode, difficulty);
    } else if (mode === 'item') {
      collectionName = 'scoresitem';
    } else {
      collectionName = 'scores';
    }

    const scoresRef = collection(db, collectionName);

    // „ÇØ„Ç®„É™„ÅÆË®≠ÂÆö
    const queryConstraints = [
      orderBy('score', 'desc'),
      limit(10)
    ];

    const q = query(scoresRef, ...queryConstraints);

    // „É™„Ç¢„É´„Çø„Ç§„É†„É™„Çπ„Éä„Éº„ÇíË®≠ÂÆö
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const scores = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        date: doc.data().date.toDate().toLocaleString(),
        items: doc.data().items || [] // „Ç¢„Ç§„ÉÜ„É†ÊÉÖÂ†±„ÇíÂèñÂæó
      }));

      // ÂêåÁÇπ„ÅÆ„Çπ„Ç≥„Ç¢„Å´Âêå„ÅòÈ†Ü‰Ωç„Çí‰ªò„Åë„Çã
      let currentRank = 1;
      let prevScore = null;

      const rankedScores = scores.map((score) => {
        if (prevScore !== null && score.score !== prevScore) {
          currentRank = scores.filter(s => s.score > score.score).length + 1;
        }
        prevScore = score.score;
        return {
          ...score,
          rank: currentRank
        };
      });

      const topScores = rankedScores.filter(score => score.rank <= 5);
      setTopScores(topScores);
    });

    return () => unsubscribe();
  }, [mode, difficulty]);

  // Âà∂ÈôêÊôÇÈñì„ÅÆÂá¶ÁêÜ
  useEffect(() => {
    if (mode !== 'time' || !isPlaying) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 0) {
          clearInterval(timer);
          setIsTimeUp(true);
          setIsPlaying(false);
          // „Çπ„Ç≥„Ç¢„ÅÆË®òÈå≤„ÅØÂà•„ÅÆuseEffect„ÅßË°å„ÅÜ
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [isPlaying, mode]);

  // ÊôÇÈñìÂàá„ÇåÊôÇ„ÅÆ„Çπ„Ç≥„Ç¢Ë®òÈå≤Áî®„ÅÆuseEffect
  useEffect(() => {
    if (mode === 'time' && timeLeft === 0 && !isPlaying && !gameOver) {
      updateScores(score);
    }
  }, [mode, timeLeft, isPlaying, gameOver, score, updateScores]);

  // „Ç≤„Éº„É†„Ç™„Éº„Éê„ÉºÊôÇ„ÅÆÂá¶ÁêÜ„Çí‰øÆÊ≠£
  useEffect(() => {
    if (!gameOver) return;

    if (mode === 'time' && timeLeft > 0) {
      // Âà∂ÈôêÊôÇÈñì„É¢„Éº„Éâ„Åß„ÅØÂç≥Â∫ß„Å´ÂÜçÈñã
      setTimeLeft(prev => Math.max(0, prev - settings.penaltyTime));
      setGameOver(false);
      setSnake(initialSnake);
      setDirection('RIGHT');
      setIsPlaying(true);
      generateFood(); // Êñ∞„Åó„ÅÑÈ§å„ÇíÁîüÊàê
    }
  }, [gameOver, mode, timeLeft, settings, initialSnake, generateFood]);

  // „Ç≤„Éº„É†„Ç™„Éº„Éê„ÉºÊôÇ„ÅÆ„Çπ„Ç≥„Ç¢Êõ¥Êñ∞„ÇíËøΩÂä†
  useEffect(() => {
    if (gameOver && score > 0 && mode !== 'time') {
      updateScores(score);
    }
  }, [gameOver, score, mode, updateScores]);

  // „Éè„Ç§„Çπ„Ç≥„Ç¢„ÅÆÊõ¥Êñ∞Âá¶ÁêÜ„ÇíËøΩÂä†
  useEffect(() => {
    if (score > highScore) {
      setHighScore(score);
      localStorage.setItem('snakeHighScore', score.toString());
    }
  }, [score, highScore]);

  // Â£Å„Å®„ÅÆË°ùÁ™ÅÂà§ÂÆö„Çí‰øÆÊ≠£Ôºà„Ç∑„Éº„É´„ÉâÂäπÊûú„ÅÆÈÅ©Áî®Ôºâ
  const checkCollision = useCallback((head) => {
    const hasShield = activeItems.find(item => item.id === 'shield' && item.isActive);

    if (hasShield) return false;

    return (
      head[0] < 0 ||
      head[0] >= gridSize ||
      head[1] < 0 ||
      head[1] >= gridSize ||
      snake.some(segment => segment[0] === head[0] && segment[1] === head[1])
    );
  }, [activeItems, gridSize, snake]);

  return (
    <div className="snake-game">
      <h2>
        {mode === 'time' ? '„Çπ„Éç„Éº„ÇØ„Ç≤„Éº„É† - Âà∂ÈôêÊôÇÈñì„É¢„Éº„Éâ' : 
         mode === 'difficulty' ? `„Çπ„Éç„Éº„ÇØ„Ç≤„Éº„É† - ${settings.label}` : 
         '„Çπ„Éç„Éº„ÇØ„Ç≤„Éº„É†'}
      </h2>
      {mode === 'time' && (
        <div className="time-left">ÊÆã„ÇäÊôÇÈñì: {timeLeft}Áßí</div>
      )}
      <div className="score-container">
        <div className="score">ÁèæÂú®„ÅÆ„Çπ„Ç≥„Ç¢: {score}</div>
        <div className="high-score">„Éè„Ç§„Çπ„Ç≥„Ç¢: {highScore}</div>
      </div>
      <div className="game-board">
        {Array.from({ length: gridSize }, (_, row) => (
          <div key={row} className="row">
            {Array.from({ length: gridSize }, (_, col) => {
              const isSnake = snake.some(
                segment => segment[0] === col && segment[1] === row
              );
              const isFood = food[0] === col && food[1] === row;
              const isSpecialFood = specialFood && 
                specialFood.position[0] === col && 
                specialFood.position[1] === row;

              let cellClass = 'cell';
              if (isSnake) cellClass += ' snake';
              if (isFood) cellClass += ' food';
              if (isSpecialFood) cellClass += ` special-food-${specialFood.type}`;

              return (
                <div
                  key={`${row}-${col}`}
                  className={cellClass}
                />
              );
            })}
          </div>
        ))}
      </div>
      <div className="controls">
        {!isPlaying && !gameOver && !isTimeUp && (
          <button onClick={startGame} className="start-button">
            „Çπ„Çø„Éº„Éà
          </button>
        )}
        {isPlaying && (
          <button onClick={() => setIsPlaying(false)} className="pause-button">
            ‰∏ÄÊôÇÂÅúÊ≠¢
          </button>
        )}
        {(gameOver && mode !== 'time') && (
          <button onClick={resetGame} className="retry-button">
            „ÇÇ„ÅÜ‰∏ÄÂ∫¶„Éó„É¨„Ç§
          </button>
        )}
      </div>
      <div className="ranking">
        <h3>Ê≠¥‰ª£„Éà„ÉÉ„Éó5</h3>
        <div className="ranking-list">
          {topScores.map((score) => (
            <div key={score.id} className="ranking-item">
              <span className="rank">{score.rank}‰Ωç</span>
              <span className="score-value">{score.score}ÁÇπ</span>
              {mode === 'item' && score.items && (
                <span className="items-used">
                  ‰ΩøÁî®„Ç¢„Ç§„ÉÜ„É†: {score.items.map(item => {
                    const itemInfo = items.find(i => i.id === item);
                    return itemInfo ? itemInfo.icon : '';
                  }).join(' ')}
                </span>
              )}
              <span className="score-date">{score.date}</span>
            </div>
          ))}
        </div>
      </div>
      {/* „Ç¢„Ç§„ÉÜ„É†ÊÉÖÂ†±„ÅÆË°®Á§∫ */}
      {mode === 'item' && (
        <div className="active-items">
          {activeItems.map((item, index) => (
            <div key={item.id} className={`item-status ${item.isActive ? 'active' : ''}`}>
              <div className="item-key">{index + 1}</div>
              <div className="item-icon">
                {item.id === 'speed' && '‚ö°'}
                {item.id === 'shield' && 'üõ°Ô∏è'}
                {item.id === 'magnet' && 'üß≤'}
                {item.id === 'double' && '‚ú®'}
              </div>
              {item.isActive && <div className="item-timer">{item.timeLeft}Áßí</div>}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SnakeGame;
