import React, { useState } from 'react';
import SnakeGame from './SnakeGame';
import '../styles/ItemMode.css';

const ItemMode = () => {
  const [selectedItems, setSelectedItems] = useState([]);
  const [isGameStarted, setIsGameStarted] = useState(false);

  const items = [
    {
      id: 'speed',
      name: '„Çπ„Éî„Éº„Éâ„Éñ„Éº„Çπ„Éà',
      description: '10ÁßíÈñì„Çπ„Éî„Éº„Éâ„Åå2ÂÄç„Å´„Å™„Çã',
      icon: '‚ö°'
    },
    {
      id: 'shield',
      name: 'ÁÑ°Êïµ',
      description: '5ÁßíÈñìÂ£Å„Å®„Éò„Éì„ÅÆË°ùÁ™Å„ÇíÁÑ°ÂäπÂåñ',
      icon: 'üõ°Ô∏è'
    },
    {
      id: 'magnet',
      name: '„Éû„Ç∞„Éç„ÉÉ„Éà',
      description: '10ÁßíÈñì„Ç®„Çµ„ÅåËá™ÂãïÁöÑ„Å´„Éò„Éì„Å´Âºï„ÅçÂØÑ„Åõ„Çâ„Çå„Çã',
      icon: 'üß≤'
    },
    {
      id: 'double',
      name: '„ÉÄ„Éñ„É´„Çπ„Ç≥„Ç¢',
      description: '15ÁßíÈñìÂæóÁÇπ„Åå2ÂÄç„Å´„Å™„Çã',
      icon: '‚ú®'
    }
  ];

  const toggleItem = (itemId) => {
    if (selectedItems.includes(itemId)) {
      setSelectedItems(prev => prev.filter(id => id !== itemId));
    } else if (selectedItems.length < 2) { // ÊúÄÂ§ß2„Å§„Åæ„ÅßÈÅ∏ÊäûÂèØËÉΩ
      setSelectedItems(prev => [...prev, itemId]);
    }
  };

  const startGame = () => {
    if (selectedItems.length > 0) {
      setIsGameStarted(true);
    }
  };

  if (isGameStarted) {
    return <SnakeGame mode="item" selectedItems={selectedItems} />;
  }

  return (
    <div className="item-mode">
      <h2>„Ç¢„Ç§„ÉÜ„É†ÈÅ∏Êäû</h2>
      <p className="item-instruction">„Ç¢„Ç§„ÉÜ„É†„Çí2„Å§„Åæ„ÅßÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ</p>
      <div className="items-grid">
        {items.map(item => (
          <div
            key={item.id}
            className={`item-card ${selectedItems.includes(item.id) ? 'selected' : ''}`}
            onClick={() => toggleItem(item.id)}
          >
            <div className="item-icon">{item.icon}</div>
            <h3>{item.name}</h3>
            <p>{item.description}</p>
          </div>
        ))}
      </div>
      <button
        className={`start-button ${selectedItems.length === 0 ? 'disabled' : ''}`}
        onClick={startGame}
        disabled={selectedItems.length === 0}
      >
        „Ç≤„Éº„É†ÈñãÂßã
      </button>
    </div>
  );
};

export default ItemMode;
