import React, { useState, useEffect, useCallback, useRef } from 'react';
import '../styles/SnakeGame.css';

const SnakeGame = () => {
  // グリッドの中央を計算
  const gridSize = 20;
  const centerX = Math.floor(gridSize / 2);
  const centerY = Math.floor(gridSize / 2);

  // 中央から右向きに3マス分の初期位置を設定
  const initialSnake = [
    [centerX, centerY],     // 頭
    [centerX - 1, centerY], // 胴体
    [centerX - 2, centerY]  // 尾
  ];

  const [snake, setSnake] = useState(initialSnake);
  const [food, setFood] = useState([0, 0]);
  const [direction, setDirection] = useState('RIGHT');
  const directionRef = useRef(direction);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [highScore, setHighScore] = useState(() => {
    const saved = localStorage.getItem('snakeHighScore');
    return saved ? parseInt(saved) : 0;
  });
  const [topScores, setTopScores] = useState(() => {
    const saved = localStorage.getItem('snakeTopScores');
    return saved ? JSON.parse(saved) : [];
  });

  const calculateSpeed = useCallback((score) => {
    const baseSpeed = 200;
    const speedIncrease = Math.floor(score / 5) * 10;
    const speed = Math.max(60, baseSpeed - speedIncrease);
    return speed;
  }, []);

  const generateFood = useCallback(() => {
    let newFood;
    do {
      newFood = [
        Math.floor(Math.random() * gridSize),
        Math.floor(Math.random() * gridSize),
      ];
    } while (snake.some(segment => segment[0] === newFood[0] && segment[1] === newFood[1]));
    setFood(newFood);
  }, [snake]);

  useEffect(() => {
    directionRef.current = direction;
  }, [direction]);

  const moveSnake = useCallback(() => {
    if (gameOver || !isPlaying) return;

    const newSnake = [...snake];
    const head = [...newSnake[0]];

    switch (directionRef.current) {
      case 'UP':
        head[1] -= 1;
        break;
      case 'DOWN':
        head[1] += 1;
        break;
      case 'LEFT':
        head[0] -= 1;
        break;
      case 'RIGHT':
        head[0] += 1;
        break;
      default:
        break;
    }

    if (
      head[0] < 0 ||
      head[0] >= gridSize ||
      head[1] < 0 ||
      head[1] >= gridSize
    ) {
      setGameOver(true);
      return;
    }

    if (newSnake.some(segment => segment[0] === head[0] && segment[1] === head[1])) {
      setGameOver(true);
      return;
    }

    newSnake.unshift(head);

    if (head[0] === food[0] && head[1] === food[1]) {
      setScore(prev => {
        const newScore = prev + 1;
        if (newScore > highScore) {
          setHighScore(newScore);
          localStorage.setItem('snakeHighScore', newScore.toString());
        }
        return newScore;
      });
      generateFood();
    } else {
      newSnake.pop();
    }

    setSnake(newSnake);
  }, [snake, food, gameOver, generateFood, highScore, isPlaying]);

  useEffect(() => {
    if (!isPlaying && !gameOver) {
      generateFood();
    }
  }, []);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'ArrowUp' || 
          e.key === 'ArrowDown' || 
          e.key === 'ArrowLeft' || 
          e.key === 'ArrowRight' || 
          e.code === 'Space') {
        e.preventDefault();
      }

      if (e.code === 'Space') {
        if (gameOver) {
          resetGame();
        } else {
          setIsPlaying(prev => !prev);
        }
        return;
      }

      if (!isPlaying) return;

      switch (e.key) {
        case 'ArrowUp':
          if (directionRef.current !== 'DOWN') setDirection('UP');
          break;
        case 'ArrowDown':
          if (directionRef.current !== 'UP') setDirection('DOWN');
          break;
        case 'ArrowLeft':
          if (directionRef.current !== 'RIGHT') setDirection('LEFT');
          break;
        case 'ArrowRight':
          if (directionRef.current !== 'LEFT') setDirection('RIGHT');
          break;
        default:
          break;
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    const gameInterval = setInterval(moveSnake, calculateSpeed(score));

    return () => {
      document.removeEventListener('keydown', handleKeyPress);
      clearInterval(gameInterval);
    };
  }, [moveSnake, gameOver, isPlaying, score, calculateSpeed]);

  const resetGame = () => {
    setSnake(initialSnake);
    setDirection('RIGHT');
    setGameOver(false);
    setScore(0);
    setIsPlaying(false);
    generateFood();
  };

  const startGame = () => {
    if (!isPlaying && !gameOver) {
      setIsPlaying(true);
    }
  };

  const updateScores = useCallback((newScore) => {
    setTopScores(prevScores => {
      const newScores = [...prevScores, { score: newScore, date: new Date().toLocaleString() }]
        .sort((a, b) => b.score - a.score)
        .slice(0, 5);
      localStorage.setItem('snakeTopScores', JSON.stringify(newScores));
      return newScores;
    });
  }, []);

  useEffect(() => {
    if (gameOver && score > 0) {
      updateScores(score);
    }
  }, [gameOver, score, updateScores]);

  return (
    <div className="snake-game">
      <h2>スネークゲーム</h2>
      <div className="score-container">
        <div className="score">現在のスコア: {score}</div>
        <div className="high-score">ハイスコア: {highScore}</div>
      </div>
      <div className="ranking">
        <h3>歴代トップ5</h3>
        <div className="ranking-list">
          {topScores.map((score, index) => (
            <div key={index} className="ranking-item">
              <span className="rank">{index + 1}位</span>
              <span className="score-value">{score.score}点</span>
              <span className="score-date">{score.date}</span>
            </div>
          ))}
        </div>
      </div>
      <div className="game-board">
        {Array.from({ length: gridSize }, (_, row) => (
          <div key={row} className="row">
            {Array.from({ length: gridSize }, (_, col) => {
              const isSnake = snake.some(
                segment => segment[0] === col && segment[1] === row
              );
              const isFood = food[0] === col && food[1] === row;
              return (
                <div
                  key={`${row}-${col}`}
                  className={`cell ${isSnake ? 'snake' : ''} ${
                    isFood ? 'food' : ''
                  }`}
                />
              );
            })}
          </div>
        ))}
      </div>
      <div className="controls">
        {!isPlaying && !gameOver && (
          <button onClick={startGame} className="start-button">
            スタート
          </button>
        )}
        {isPlaying && (
          <button onClick={() => setIsPlaying(false)} className="pause-button">
            一時停止
          </button>
        )}
        {gameOver && (
          <button onClick={resetGame} className="retry-button">
            もう一度プレイ
          </button>
        )}
      </div>
      <div className="instructions">
        <p>操作方法:</p>
        <ul>
          <li>矢印キーで移動</li>
          <li>スペースキーで開始/一時停止</li>
        </ul>
      </div>
    </div>
  );
};

export default SnakeGame;
